package com.example.ibraimkulov_hw4_m3_aaa.continent;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.ibraimkulov_hw4_m3_aaa.country.CountryFragment;
import com.example.ibraimkulov_hw4_m3_aaa.OnClick;
import com.example.ibraimkulov_hw4_m3_aaa.R;
import com.example.ibraimkulov_hw4_m3_aaa.databinding.FragmentContinentBinding;

import java.util.ArrayList;

public class ContinentFragment extends Fragment implements OnClick {
    private FragmentContinentBinding binding;
    private ArrayList<ContinentModel> continentList = new ArrayList<>();

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentContinentBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        loadData();
        initAdapter();

    }

    private void initAdapter() {
        ContinentAdapter continentAdapter = new ContinentAdapter(continentList, this);
        binding.recyclerView.setAdapter(continentAdapter);
    }

    private void loadData() {
        continentList.add(new ContinentModel("data:image/jpeg;base64,", "Eurasia"));
        continentList.add(new ContinentModel("data:image/jpeg;base64,", "North America"));
        continentList.add(new ContinentModel("data:image/jpeg;base64,", "South America"));
        continentList.add(new ContinentModel("data:image/jpeg;base64,", "Africa"));
        continentList.add(new ContinentModel("data:image/jpeg;base64,", "Australia"));
    }

    @Override
    public void onClick(int position) {
        Bundle bundle = new Bundle();
        bundle.putInt("keyForPosition", position);
        CountryFragment countryFragment = new CountryFragment();
        countryFragment.setArguments(bundle);
        requireActivity().getSupportFragmentManager().beginTransaction().replace(R.id.container, countryFragment).addToBackStack(null).commit();
    }
}